DROP TABLE IF EXISTS `view_articles`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_articles` AS select `art`.`idArticle` AS `idArticle`,`art`.`titreArticle` AS `titreArticle`,`art`.`contenuArticle` AS `contenuArticle`,`art`.`autheur` AS `autheur`,`art`.`dateArticle` AS `dateArticle`,`art`.`postName` AS `postName`,`art`.`mediaArticle` AS `mediaArticle`,`cat`.`nomCategorie` AS `nomCategorie`,`cat`.`categorieterms` AS `categorieterms`,`cat`.`imageCategorie` AS `imageCategorie`,`statut`.`statut` AS `statut`,`lang`.`langues` AS `langues`,`art`.`extrait` AS `extrait` from (((`articles` `art` join `categories` `cat` on((`cat`.`idCategorie` = `art`.`categories_idCategorie`))) join `statuts` `statut` on((`statut`.`idStatut` = `art`.`statuts_idStatut`))) join `langues` `lang` on((`lang`.`idLangues` = `art`.`idLangues`))) order by `art`.`dateArticle` desc;

